import React from 'react'
import { useState ,useEffect } from 'react';
import {useParams} from 'react-router-dom';
import Data from '../components/data.json';

function Article(){

    const [article, articleState] = useState({});

    let {id} = useParams(); 

    useEffect(()=>{
        const dataToSet = Data.find((item) => item.id === id)
        articleState(dataToSet)

    }, [id]);

    return (
        <main>
            <section className="ArticleHeader">
                <div className="ArticleHeaderText">
                    <h1 className="HeaderOneStyle">{article.title}</h1>
                    <p className="ArticleCardDate">{article.publishedDate}</p>
                    <p className="ArticleBlurb">{article.blurb}</p>
                </div>
            </section>
            <section className="ArticleText">
                <h2>Header Two Inserted</h2>
                <p>     </p>

                {/* {article.articleText.map((a, key) => (
                    {a = a, key=key}
                ))} */}

                <h3>Header Three Inserted</h3>
                <p>Dsafdafdaf</p>
            </section>
        </main>
    );
}

export default Article;
